package com.example.kraftopia.ui.main.shop

import android.content.Context
import android.graphics.Paint
import androidx.lifecycle.ViewModelProvider
import android.os.Bundle
import android.renderscript.ScriptGroup
import android.text.Html
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.databinding.DataBindingUtil
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.kraftopia.R
import com.example.kraftopia.adapter.*
import com.example.kraftopia.databinding.BannerShopBinding
import com.example.kraftopia.databinding.FragmentShopBinding
import com.smarteist.autoimageslider.SliderView
import dagger.hilt.android.AndroidEntryPoint

@AndroidEntryPoint
class Shop : Fragment() {

    lateinit var binding: FragmentShopBinding

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = DataBindingUtil.inflate(inflater, R.layout.fragment_shop, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        val moreText: TextView = view.findViewById(R.id.category_more_text)
        moreText.paintFlags = Paint.UNDERLINE_TEXT_FLAG
        val data = listOf(
            BannerItem("https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.dreamstime.com%2Fphotos-images%2Fpuppies-banner.html&psig=AOvVaw3kaJziHQoPSdyK_5NbqBwi&ust=1625572297602000&source=images&cd=vfe&ved=0CAoQjRxqFwoTCMClv-_uy_ECFQAAAAAdAAAAABAD"),
            BannerItem(
                "data:image/jpeg;base64,"
            ),
            BannerItem(
                "data:image/jpeg;base64,"
            )
        )
        val bannerSlider = view.findViewById<SliderView>(R.id.banner_slider)
        val bannerAdapter = BannerAdapter(requireActivity().applicationContext, data)
        bannerSlider.apply {
            setSliderAdapter(bannerAdapter)
            scrollTimeInSec = 3
            isAutoCycle = true
            startAutoCycle()
        }
        val categoryData = listOf(
            CategoryItem(
                "data:image/jpeg;base64,",
                "Paper Flower"
            ),
            CategoryItem(
                "data:image/jpeg;base64,",
                "Paper Ok"
            ),
            CategoryItem(
                "data:image/jpeg;base64,",
                " Flower"
            ),
            CategoryItem(
                "data:image/jpeg;base64,",
                "Paper"
            ),

            )

        val categoryAdapter = CategoryAdapter(requireActivity().applicationContext, categoryData)
        binding.includeCategoryGrid.categoryRecyclerView.apply {
            layoutManager = GridLayoutManager(activity, 2)
            adapter = categoryAdapter
        }
        val productData = listOf<LatestProductItem>(
            LatestProductItem("https://images.news18.com/ibnlive/uploads/2021/04/1618806057_shutterstock_1679527528.jpg", "Dot Painting", "330.00"),
            LatestProductItem("https://images.immediate.co.uk/production/volatile/sites/32/2020/03/Easy-craft-ideas-for-kids-to-make-at-home-531ddde.png?quality=90&resize=620,413", "Rock Painting", "1100.00"),
            LatestProductItem("https://www.thesprucecrafts.com/thmb/tWcuMWFM3d7ee_A3sPmxQcN28mM=/1733x975/smart/filters:no_upscale()/Stocksy_txp85410d10lCe200_Medium_2971014-ef1faa7eea7f4644b13c8d524aa1ee69.jpg", "Craft Pebble", "4455.00"),
            LatestProductItem("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTRFoNVwV4metMEOXAkK24fFciEhv__qKBKSg&usqp=CAU", "White Pillow", "299.00")
        )
        val latestProductAdapter=LatestProductAdapter(requireActivity().applicationContext,productData)
        binding.includeLatestProductGrid.latestProductRecyclerView.apply {
            layoutManager=LinearLayoutManager(activity,LinearLayoutManager.HORIZONTAL,false)
            adapter=latestProductAdapter
        }
        super.onViewCreated(view, savedInstanceState)
    }

}